import React, { useState, useEffect } from "react";
import "./App.css";

import Messages from "./components/Messages";
import Input from "./components/Input";
import Navigation from "./components/Navigation";
import Footer from "./components/Footer";

import PopUp from "./components/PopUp";
import ActiveUsers from "./ActiveUsers"; // Import ActiveUsers component from the new file

import Container from "react-bootstrap/Container";
import Card from "react-bootstrap/Card";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";

function App() {

const randomName = () => {
	const colors = [
	"red",
	"blue",
	"green",
	"black",
	"orange",
	"pink",
	"purple",
	"cyan",
	"yellow",
	];
	const animals = [
	"Panther",
	"Panda",
	"Lion",
	"Doggo",
	"Kitty",
	"Bird",
	"Piggy",
	"Salmon",
	"Giraffe",
	];
	const color = colors[Math.floor(Math.random() * colors.length)];
	const animal = animals[Math.floor(Math.random() * animals.length)];
	return color + animal;
};

const randomAvatar = () => {
	const avatars = [
	"/assets/images/SCR-20230724-mist.png",
	"/assets/images/SCR-20230724-mivs.png",
	"/assets/images/SCR-20230724-miwx.png",
	"/assets/images/SCR-20230724-mixz.png",
	"/assets/images/SCR-20230724-mizm.png",
	"/assets/images/SCR-20230724-mjaz.png",
	];
	const avatar = avatars[Math.floor(Math.random() * avatars.length)];
	return avatar;
};

const initialState = {
	messages: [],
	member: {
	username: randomName(),
	avatar: randomAvatar(),
	},
	activeUsers: [], // Array to store active users
};
//handling states to display active users
const [state, setState] = useState(initialState);
const [drone, setDrone] = useState(null);

const [uniqueActiveUsers, setUniqueActiveUsers] = useState(new Set());

const updateActiveUsers = (members) => {
	const activeUsers = members.map((member) => ({
	username: member.clientData.username,
	avatar: member.clientData.avatar,
	}));

	const uniqueUsersSet = new Set(activeUsers.map((user) => user.username));
	setUniqueActiveUsers(uniqueUsersSet);

	setState((prevState) => ({ ...prevState, activeUsers }));
};

//handling image that pops up on load
const [showImagePopup, setShowImagePopup] = useState(true);

const popupImageUrl = "./assets/images/anychat.png";

//scaledrone
useEffect(() => {
	const newDrone = new window.Scaledrone("wiCb9iY1E4xejxZe", {
	data: state.member,
	});

	setDrone(newDrone);

	const room = newDrone.subscribe("observable-room");

	newDrone.on("open", (error) => {
	if (error) {
		return console.error(error);
	}
	const member = { ...state.member, avatar: randomAvatar() };
	member.id = newDrone.clientId;
	setState((prevState) => ({ ...prevState, member }));
	});

	room.on("data", (data, member) => {
	setState((prevState) => {
		const newMessages = [...prevState.messages, { member, text: data }];
		return { ...prevState, messages: newMessages };
	});
	});

	room.on("members", (members) => {
	updateActiveUsers(members); // Update active users when members change
	});

	const timer = setTimeout(() => {
	setShowImagePopup(false);
	}, 3000);

	return () => {
	room.unsubscribe();
	newDrone.close();
	clearTimeout(timer)
	};
}, []);

const onSendMessage = (message) => {
	drone.publish({
	room: "observable-room",
	message,
	});
};

//active users bootstrap kartice
const ActiveUsers = ({ activeUsers }) => {
	return (
	<Container style={{paddingBottom:"20px", borderRadius:"5px", color: "white"}} fluid className="active-users text-center">
		<h2>Active Users</h2>
		<Row sm={3} className="g-4">
		{activeUsers.map((user, index) => (
			<Col key={index}>
			<Card style={{ backgroundColor: "#0474ab", borderRadius:"5px", padding:"5px" }} className="card-small text-center">
				<Card.Img
				className="avatar"
				variant="top"
				src={user.avatar}
				alt={user.username}
				/>
				<Card.Body>
				<Card.Title className="card-title">
					{user.username}
				</Card.Title>
				</Card.Body>
			</Card>
			</Col>
		))}
		</Row>
	</Container>
	);
};

return (
	<Container fluid="lg" style={{ backgroundColor: "#2c2c2c" }}>
	<div className="App">
		<Navigation />
		<div className="messages-container">
			<Messages messages={state.messages} currentMember={state.member} />
		</div>
		<Input onSendMessage={onSendMessage} />
		<ActiveUsers
			activeUsers={Array.from(uniqueActiveUsers).map((username) =>
				state.activeUsers.find((user) => user.username === username)
			)}
		/>
		{showImagePopup && <PopUp imageUrl={popupImageUrl} />}
	</div>
	<Footer />
	</Container>
);
}

export default App;
