import React from "react";

export default function SingleMessage({ message, currentMember }) {
  const { member, text } = message; // Destructure the 'message' object
  const messageFromMe = member?.clientData?.username === currentMember.username; // Use optional chaining to avoid errors
  const className = messageFromMe
    ? "messages-message currentMember"
    : "messages-message";

  return (
    <li className={className}>
      <img className="avatar" src={member?.clientData?.avatar} alt={member?.clientData?.avatar} /> {/* Use optional chaining */}
      <div className="message-content">
        <div className="username">{member?.clientData?.username}</div> {/* Use optional chaining */}
        <div className="text">{text}</div>
      </div>
    </li>
  );
}
