import React from "react";

export default function Messages({ messages, currentMember }) {
	const renderMessage = (message) => {
		const member = message.member.clientData;
		const text = message.text;
		const messageFromMe = member.username === currentMember.username; //
		const className = messageFromMe
			? "Messages-message currentMember"
			: "Messages-message";

		return (
			<li className={className}>
				<img className="avatar" src={member.avatar} alt={member.avatar} />
				<div className="message-content">
					<div className="username">{member.username}</div>
					<div className="text">{text}</div>
				</div>
			</li>
		);
	};

	return (
		<ul className="messages-list">
  			{messages.map((m, index) => (
    			[<li key={index}>{renderMessage(m)}</li>]
  			))}
		</ul>
	);
}
