import React from "react";

export default function SingleMessage({ message, currentMember }) {
  const member = message?.member?.clientData || {}; // Handle undefined case with empty object
  const text = message?.text || "";
  const messageFromCurrentUser = member.username === currentMember.username;
  const className = messageFromCurrentUser
    ? "messages-message currentMember"
    : "messages-message";

  return (
    <li className={className}>
      <img className="avatar" src={member.avatar} alt={member.avatar} />
      <div className="message-content">
        <div className="username">{member.username}</div>
        <div className="text">{text}</div>
      </div>
    </li>
  );
}
